import React, { Component } from 'react';

class Header extends Component {

      constructor(props) {
      super(props);

      this.addActiveClass= this.addActiveClass.bind(this);
      this.state = {
        isActive: false
      }
    }

    addActiveClass() {
      this.setState({
        isActive: true
      })
    }
  render() {

    if(this.props.data){
      var name = this.props.data.name;
      var occupation= this.props.data.occupation;
      var description= this.props.data.description;
      var city= this.props.data.address.city;
      var networks= this.props.data.social.map(function(network){
        return <li key={network.name}><a href={network.url}><i className={network.className}></i></a></li>
      })
    }

    return (
      <header id="home">




      <nav id="nav-wrap">
      <div id="sidebar" ref="sidebar">
        <div className={this.state.isActive ? 'active' : ''} ref="btn" onclick={this.addActiveClass}>
        <span />
        <span />
        <span />
        </div>
        <ul>
          <a href="index.html" class="animsition-link">
            <li>Home</li>
          </a>

          <a href="about.html" class="animsition-link">
            <li>About</li>
          </a>

          <a href="resume.html" class="animsition-link">
            <li>Resume</li>
          </a>

          <a href="projects.html" class="animsition-link">
            <li>Projects</li>
          </a>
        </ul>

      </div>





        <a className="mobile-btn" href="#nav-wrap" title="Show navigation">Show navigation</a>
	      <a className="mobile-btn" href="#home" title="Hide navigation">Hide navigation</a>

         <ul id="nav" className="nav">
            <li className="current"><a className="smoothscroll" href="#home">Home</a></li>
            <li><a className="smoothscroll" href="#about">Skills</a></li>
	         <li><a className="smoothscroll" href="#resume">Resume</a></li>
            <li><a className="smoothscroll" href="#portfolio">Works</a></li>
         </ul>

      </nav>

      <div className="row banner">
         <div className="banner-text">
            <h1 className="responsive-headline">I'm {name}.</h1>
            <h3>I'm a {city} based <span>{occupation}</span>. {description}.</h3>
            <hr />
            <ul className="social">
               {networks}
            </ul>
         </div>
      </div>

      <p className="scrolldown">
         <a className="smoothscroll" href="#about"><i className="icon-down-circle"></i></a>
      </p>

   </header>
    );
  }
  toggleSidenav() {
    var node = React.render.findDOMNode(this.refs.btn);
    node.classList.toggle('active');
  }



}

export default Header;
